import React from 'react';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import logojd from '../jd.png';
import bkgrd from '../jd222.jpg';

class Main3 extends React.Component{
  constructor(props){
    super();
    this.State = {flag : 0,currValue : props.translation};
    console.log(this.State.currValue);
  }

  handleSubmit(){}

  handleChange(event){
    let def = this.props.translation;
    let currNew = event.target.value;
    this.setState(prevState =>{
      if(currNew === def)
      return {
        flag: 1,
        currValue : currNew
      }
      else {
        return {
          flag :0,
          currValue :currNew
        }
      }
    })
  }

  render(){
    let ll = (<div>
      <p> {this.props.word}
      <input type = "text" id = "inp" value = {this.props.translation} onChange = {this.handleChange}/>
      </p>
    </div>);
    if(this.State.flag === 1){
      ll.push(<input type="submit" value="Submit" className = "button button1" />);
    }
      return ll;
  }
}
export default Main3
