import React from 'react';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import logojd from '../jd.png';
import bkgrd from '../jd222.jpeg';

class Main extends React.Component {

  constructor(props) {
    super(props);
    this.state = { value: "coconut" };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    alert("Your favorite flavor is: " + this.state.value);
    event.preventDefault();
  }

  handleChange = event => {
    this.setState({ value: event.target.value });
  };

  render() {
    return (
      <div style={{
        height: 1000,
        backgroundImage: `url(${bkgrd})`,
        backgroundPosition: 'center',
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat'
      }}>
      <form onSubmit={this.handleSubmit}>
        <label>
          <select value={this.state.value} onChange={this.handleChange} className = "selected-option">
            <option value="grapefruit" className = "selection">Grapefruit</option>
            <option value="lime" className = "selection">Lime</option>
            <option value="coconut" className = "selection">Coconut</option>
            <option value="mango" className = "selection">Mango</option>
          </select>
        </label>
        <input type="submit" value="Submit" className = "button button1" />
      </form>
      </div>
    );
  }
}

export default Main
