  }
  handleSubmit(){

  }
  handleChange(event){
    let e = this.state.currValue;
    let def = this.props.translation;
    let currNew = event.target.value;
    if(currNew === def){
      this.setState({flag:0, currValue: currNew});
    }
    else{
      this.setState({flag:1, currValue : currNew});
    }
  }
  render(){

    let ll = (<div>
